import { useCallback, useReducer, useEffect, useState, useMemo } from "react";
import { EAST, INIT_STATE, NORTH, SOUTH, WEST } from "../initialState.js";
import { random_food } from "../logic.js";

function useKeyPress(targetKey) {
  // State for keeping track of whether key is pressed
  const [keyPressed, setKeyPressed] = useState(false);
  // If pressed key is our target key then set to true
  const downHandler = useCallback(
    ({ key }) => {
      if (key === targetKey) {
        setKeyPressed(true);
      }
    },
    [targetKey]
  );
  // If released key is our target key then set to false
  const upHandler = useCallback(
    ({ key }) => {
      if (key === targetKey) {
        setKeyPressed(false);
      }
    },
    [targetKey]
  );
  // Add event listeners
  useEffect(() => {
    window.addEventListener("keydown", downHandler);
    window.addEventListener("keyup", upHandler);
    // Remove event listeners on cleanup
    return () => {
      window.removeEventListener("keydown", downHandler);
      window.removeEventListener("keyup", upHandler);
    };
  }, [downHandler, upHandler]); // Empty array ensures that effect is only run on mount and unmount
  return keyPressed;
}

export default function useSnake() {
  const [currentDirection, setCurrentDirection] = useState();

  const wPress = useKeyPress("w");
  const sPress = useKeyPress("s");
  const aPress = useKeyPress("a");
  const dPress = useKeyPress("d");

  const snakeRedcuer = (state, action) => {
    switch (action.type) {
      case "MOVE":
        const snake = state.snake;
        snake.unshift({
          x: state.snake[0].x + action.direction.x,
          y: state.snake[0].y + action.direction.y,
        });

        return {
          ...state,
          moves: [...state.moves, action.direction],
          snake: snake,
        };
      case "LOCATE":
        return {
          ...state,
          snake: [
            ...state.snake.slice(-1),
            {
              x: action.x,
              y: action.y,
            },
          ],
        };
      case "EAT":
        let apple_x = state.apple.x;
        let apple_y = state.apple.y;

        const hasEatenFood =
          state.snake[0].x === apple_x && state.snake[0].y === apple_y;

        if (hasEatenFood) {
          const apple_x = random_food(0, state.board.width / state.cols);
          const apple_y = random_food(0, state.board.height / state.rows);
          const snake = state.snake;
          return {
            ...state,
            apple: { apple_x, apple_y },
            snake: snake,
          };
        } else if (state.snake.length > 1) {
          const snake = state.snake;
          snake.pop();
          return {
            ...state,
            snake: snake,
          };
        }
        return state;
      default:
        break;
    }
  };

  const [state, dispatch] = useReducer(snakeRedcuer, INIT_STATE);

  const moveSnakeHandler = useCallback(() => {
    switch (true) {
      case wPress:
        setCurrentDirection(NORTH);
        break;
      case sPress:
        setCurrentDirection(SOUTH);
        break;
      case aPress:
        setCurrentDirection(WEST);
        break;
      case dPress:
        setCurrentDirection(EAST);
        break;
      default:
        break;
    }
  }, [aPress, dPress, sPress, wPress]);

  useEffect(() => moveSnakeHandler(), [moveSnakeHandler]);

  // Reposition snake
  useEffect(() => {
    if (state.snake[0].x < 0)
      dispatch({ type: "LOCATE", x: state.cols - 1, y: state.snake[0].y });

    if (state.snake[0].y < 0)
      dispatch({ type: "LOCATE", x: state.snake[0].x, y: state.rows - 1 });

    if (state.snake[0].x > state.cols - 1)
      dispatch({ type: "LOCATE", x: 0, y: state.snake[0].y });

    if (state.snake[0].y > state.rows - 1)
      dispatch({ type: "LOCATE", x: state.snake[0].x, y: 0 });
  }, [state.cols, state.rows, state.snake]);

  useEffect(() => {
    const interval = setInterval(() => {
      if (currentDirection) {
        dispatch({ type: "MOVE", direction: currentDirection });
        dispatch({ type: "EAT" });
      }
    }, state.speed);

    return () => clearInterval(interval);
  }, [currentDirection, state.moves, state.speed]);

  console.log(state);
  return state;
}
