import { useCallback, useReducer, useEffect, useState } from "react";
import { EAST, INIT_STATE, NORTH, SOUTH, WEST } from "../initialState.js";

function useKeyPress(targetKey) {
  // State for keeping track of whether key is pressed
  const [keyPressed, setKeyPressed] = useState(false);
  // If pressed key is our target key then set to true
  const downHandler = useCallback(
    ({ key }) => {
      if (key === targetKey) {
        setKeyPressed(true);
      }
    },
    [targetKey]
  );
  // If released key is our target key then set to false
  const upHandler = useCallback(
    ({ key }) => {
      if (key === targetKey) {
        setKeyPressed(false);
      }
    },
    [targetKey]
  );
  // Add event listeners
  useEffect(() => {
    window.addEventListener("keydown", downHandler);
    window.addEventListener("keyup", upHandler);
    // Remove event listeners on cleanup
    return () => {
      window.removeEventListener("keydown", downHandler);
      window.removeEventListener("keyup", upHandler);
    };
  }, [downHandler, upHandler]); // Empty array ensures that effect is only run on mount and unmount
  return keyPressed;
}

export default function useSnake() {
  const wPress = useKeyPress("w");
  const sPress = useKeyPress("s");
  const aPress = useKeyPress("a");
  const dPress = useKeyPress("d");

  const snakeRedcuer = (state, action) => {
    switch (action.type) {
      case "MOVE":
        if (state.moves[state.moves.length - 1] !== action.direction) {
          return { ...state, moves: [...state.moves, action.direction] };
        }
        break;
      default:
        break;
    }
  };

  const [state, dispatch] = useReducer(snakeRedcuer, INIT_STATE);

  const moveSnakeHandler = useCallback(() => {
    switch (true) {
      case wPress:
        dispatch({ type: "MOVE", direction: NORTH });
        console.log("w");
        break;
      case sPress:
        dispatch({ type: "MOVE", direction: SOUTH });
        console.log("s");
        break;
      case aPress:
        dispatch({ type: "MOVE", direction: WEST });
        console.log("a");
        break;
      case dPress:
        dispatch({ type: "MOVE", direction: EAST });
        console.log("d");
        break;
      default:
        break;
    }
  }, [aPress, dPress, sPress, wPress]);

  useEffect(() => moveSnakeHandler(), [moveSnakeHandler]);

  useEffect(() => {
    return state;
  }, [state]);

  return state;
}
